import React,{useState,useEffect} from 'react'
import Display from './Display'
import WatchList from './WatchList'


function Renderitems() {
const [movies, setMovies] =useState([])
const [watchlist,setWatchlist]=useState([])

const AddtoWatchlist = (movie) => {
    const newwatchList = watchlist.filter((b) => b.id !== movie.id);
    setWatchlist([...newwatchList, movie]);
  };

  const removefromWatchlist = (movie) => {
    const newwatchList = watchlist.filter((b) => b.id !== movie.id);
    setWatchlist(newwatchList);
  };

useEffect(()=>{
    fetch("http://localhost:3000/movies")
    .then(res => res.json())
    .then(data=>{
        setMovies(data)
    })

},[])
  return (
    <div>
      <BrowserRouter>
      <Routes>

      </Routes>
      </BrowserRouter>
         <WatchList watchlist={watchlist} setWatchlist={setWatchlist} removefromWatchlist={removefromWatchlist}/>
        <Display movies={movies} setMovies={setMovies} AddtoWatchlist={AddtoWatchlist}/>

    </div>
  )
}

export default Renderitems
