import React from 'react'


function WatchList({watchlist,setWatchlist,removefromWatchlist}) {
  return (
    <div className='watchlist'>
    <div className=' container my-5'> 
     {/* render the bots in cards */}
    <div className=" row ">
        {
          watchlist.map((movie)=>(

          <div key={movie.id} className="col-3 mb-3  mr-3" >
          <div className='border bg-light overflow-hidden'>
             <img src={movie.cardImg} className='img-fluid ' alt='image loading...' />
            <h5 style={{textAlign:'center',fontWeight:'bolder'}}>{movie.title}</h5>
            {/* style the bot details */}
            <hr />
            <div id="bot-details">
            <p> {movie.subTitle}</p>
            <button onClick={()=>removefromWatchlist(movie)}>Remove</button>
          </div>
          </div> 
          </div>

          ))  
          }
    </div>

  </div>
  </div>

  )
}

export default WatchList
