import React from 'react'


function Display({movies,setMovies,AddtoWatchlist}) {
  const [selectedClass, setSelectedClass] = useState('');

  const handleClassChange = (e) => {
    setSelectedClass(e.target.value);
  };

  const filteredMovies = selectedClass
    ? movies.filter((movie) => movie.category === selectedClass)
    : movies;
  return (
    <div>
        <div className='filterBar'>
    <select value={selectedClass} onChange={handleClassChange}>
    <option value=''>Choose Category</option>
    <option value='Action'>Action</option>
    <option value='Drama'>Drama</option>
    <option value='Assault'>Assault</option>
    <option value='Defender'>Defender</option>
    <option value='Captain'>Captain</option>
    <option value='Witch'>Witch</option>
  </select>

</div>
         <div className=' container my-5'>
     {/* render the bots in cards */}
    <div className=" row ">
        {
          filteredMovies.map((movie)=>( 
          <div key={movie.id} className="col-3 mb-4  mr-3 px-3">
          <div className='border bg-light overflow-hidden'  >
             <img src={movie.poster} className='img-fluid ' alt='image loading...' />
            <h5 style={{textAlign:'center',fontWeight:'bolder'}}>{movie.title}</h5>
            {/* style the bot details */}
            <hr />
            <div id="bot-details">
            <p> {movie.genres}</p>
            <p>{movie.rating}</p>
            <p>{movie.language}</p>
            <button onClick={() => AddtoWatchlist(movie)}>Add to watchlist</button>
          </div>
          </div> 
          </div>
          ))  
          }
    </div>

  </div>
    </div>
  )
}

export default Display
