import React, { useState } from 'react'

function AddMovie() {

    const [type, setType]=useState("");
    const [title, setTitle]=useState("");
    const [cardImg, setCardImage]=useState("");
    const [backgroundImg, setBackgroundImg]=useState(" ");
    const [description, setDescription]=useState(" ");
    const [subTitle, setSubtitle]=useState(" ");


        const handleSubmit = (event) => {
            event.preventDefault();
            fetch("https://movies-csku.onrender.com/movies", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({
                type,
                title,
                cardImg,
                backgroundImg,
                description,
                subTitle
              }),
            })
              .then((response) => {
                return response.json();
              })
              .then((data) => {
                alert("Successfully added movie!");
              });
            setType("");
            setTitle("");
            setCardImage("");
            setBackgroundImg("");
            setDescription("");
            setSubtitle("");
          };
  return (
    <div className='add'>
    <div className='container1'>

        <div className='row my-5 '>
            <div className='col-5'>
                 <h3 style={{textAlign:'center',color:'blue'}}>ADD A MOVIE</h3>

                <form onSubmit={handleSubmit} className='justify-content-center'>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>Type</label>
                    <input type="text" value={type} onChange={(e)=> setType(e.target.value)} className="form-control" />
                </div>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>Title</label>
                    <input type="text" value={title} onChange={(e)=> setTitle(e.target.value)} className="form-control" />
                </div>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>BackGround Image</label>
                    <input type="text" value={backgroundImg} onChange={(e)=> setBackgroundImg(e.target.value)} className="form-control" />
                </div>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>Card Image</label>
                    <input type="text"  value={cardImg} onChange={(e)=> setCardImage(e.target.value)} className="form-control" />
                </div>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>Summary</label>
                    <input type="text" value={description} onChange={(e)=> setDescription(e.target.value)} className="form-control" />
                </div>
                <div className="mb-2">
                    <label className="form-label" style={{color:'white',fontWeight:'lighter'}}>Sub-Title</label>
                    <input type="text" 
                    value={subTitle} 
                    onChange={(e)=> setSubtitle(e.target.value)} 
                    style={{borderColor:"blueviolet"}}
                    className="form-control" />
                </div>

                <button type="submit" className="btn btn-primary">Submit</button>
                </form>


            </div>
        </div>
    </div>
    </div>
  )
  }
  export default AddMovie;
