import React ,{useState} from 'react'
import {NavLink} from 'react-router-dom'

function NavBar({movies,setMovies}) {

  // Search
const [searchItem, setSearchItem] = useState(" ");

//  Create a search function 
 function searchAnything(event){

    const matchItems = movies.filter((dt=>{
        return ` ${dt.title}`
        .toLowerCase()
        .includes(event.target.value.toLowerCase())
    }))
    setMovies(matchItems);
    setSearchItem(" ")

    }

  return (
    <div className='nav'>
     <nav>



    <img src='https://img.icons8.com/color/48/null/imdb.png' alt='imdb logo' className='logo'/>
    <NavLink to="/"/>
    <NavLink to="home">HOME</NavLink> 
    <div id="search-bar">
          <NavLink>
                <input 
                    type='text'
                    placeholder = "Search" 
                    onChange={searchAnything} />
                    </NavLink>

       </div>
       <NavLink to="watch">WATCHLIST</NavLink>

       <NavLink>
        <button className='login-btn'>LOGIN</button>
       </NavLink>

    </nav>
    </div>
  )
}

export default NavBar
