import React,{useState} from 'react'
import {NavLink, Outlet, useRoutes} from 'react-router-dom'
import Carousel from './Carousel';
import SingleMovie from './SingleMovie';

function Display({movies,setMovies,AddtoWatchlist}) {
  // Display specific categories
  const [selectedClass, setSelectedClass] = useState('');

  // Search
  const [searchItem, setSearchItem] = useState(" ");

  //  Create a search function 
  function searchAnything(event){
    const matchItems = movies.filter((dt=>{
        return ` ${dt.title}`
        .toLowerCase()
        .includes(event.target.value.toLowerCase())
    }))
    setMovies(matchItems);
    setSearchItem(" ")
  }

  const handleClassChange = (e) => {
    setSelectedClass(e.target.value);
  };

  const filteredMovies = selectedClass
    ? movies.filter((movie) => movie.type === selectedClass)
    : movies;

  // Define the nested routes
  const routes = [
    {
      path: 'movie/:id',
      element: <SingleMovie AddtoWatchlist={AddtoWatchlist} />
    }
  ];

  // Use the useRoutes hook to render the nested routes


  return (
    <div className='display'>
      <Carousel/>
      {/* Create search bar */}
      <div id="search-bar">
        <input 
          type='text'
          placeholder="Search" 
          onChange={searchAnything}
        />
      </div>

      <div className='filterBar'>
        <select value={selectedClass} onChange={handleClassChange}>
          <option value=''>Choose Category</option>
          <option value='new'>New</option>
          <option value='trending'>Trending</option>
          <option value='original'>Original</option>
        </select>
      </div>

      <div className='container my-5'>
        {/* Render the movies in cards */}
        <div className="row" >
          {filteredMovies.map((movie)=>( 
            <div key={movie.id} className="col-4 mb-3  mr-3 px-3">
              <div className='border bg-light overflow-hidden rounded'> 
                <NavLink to={`movie/${movie.id}`}>
                  <img src={movie.cardImg} className='img-fluid' alt='image loading...' />
                </NavLink>
                <h5 style={{textAlign:'center',fontWeight:'lighter'}}>
                  {movie.title.toUpperCase()}
                </h5>
                <div id="movie-details">
                  <p style={{fontWeight:'lighter',fontSize:'13px'}}>
                    {movie.subTitle}
                  </p>
                  <button onClick={() => AddtoWatchlist(movie)}>
                    Add to watchlist
                  </button> 
                </div>
              </div> 
            </div>
          ))}
        </div>

        {/* Render the nested routes */}
        <Outlet />
      </div>
    </div>
  );
}

export default Display;
