import React,{useState,useEffect} from 'react'
import { useParams,useNavigate} from "react-router-dom";

function SingleMovie({AddtoWatchlist}) {
    const [singlemovie,setSingleMovie] =useState({
        id: "",
        poster: "",
        title: "",
        genres: "",
        rating: "",
        language: "",
    })

    const nav = useNavigate()
    const {id} = useParams()
    useEffect(()=>{
      fetch(`http://localhost:3000/movies/${id}`)
      .then(res=>res.json())
      .then(data=>{
        setSingleMovie(data)     
      })
    }, [])
    const goBack = () => {
      nav(-1);}

  return (
    <div>
        {
          <div key={singlemovie.id} className="col-3 mb-5 mr-3 px-5 mt-5 ">
             <div className='border bg-light overflow-hidden'  >
            <button onClick={goBack}>Go back</button>
            <img src={singlemovie.poster} className='img-fluid ' alt='image loading...'/>
            <h5 style={{textAlign:'center',fontWeight:'bolder'}}>{singlemovie.title}</h5>
            {/* style the bot details */}
            <hr />
            <div id="bot-details">
            <p>{singlemovie.genres}</p>
            <p>{singlemovie.rating}</p>
            <p>{singlemovie.language}</p>
            <p>{singlemovie.likes}</p>
          </div>
          <button onClick={() => AddtoWatchlist(singlemovie.id)}>Add</button>

          </div> 
          </div> 

          }
   </div>
  )
}

export default SingleMovie
