import React,{useState} from 'react'
import {NavLink, Outlet, Route,Routes} from 'react-router-dom'
import Carousel from './Carousel';
import SingleMovie from './SingleMovie';



function Display({movies,setMovies,AddtoWatchlist}) {
    // Display specific categories
  const [selectedClass, setSelectedClass] = useState('');



  const handleClassChange = (e) => {
    setSelectedClass(e.target.value);
  };

  const filteredMovies = selectedClass
    ? movies.filter((movie) => movie.type === selectedClass)
    : movies;
  return (

    <div className='display'>


      <Carousel/>

    <div className='filterBar'>
    <select value={selectedClass} onChange={handleClassChange}>
    <option value=''>Choose Category</option>
    <option value='new'>New</option>
    <option value='trending'>Trending</option>
    <option value='original'>Original</option>
  </select>

</div>
         <div className='container my-5'>
     {/* render the bots in cards */}
    <div className=" row " >
        {
          filteredMovies.map((movie)=>( 


          <div key={movie.id} className=' card-deck overflow-hidden col-4 mb-3  mr-3 px-3 shadow g-2'> 
          <NavLink to={`movie/${movie.id}`}>
             <img src={movie.cardImg} className='card-img-top ' alt='loading...'/>
          </NavLink>
            <h5 style={{textAlign:'center',fontWeight:'lighter'}}>{movie.title.toUpperCase()}</h5>
            {/* style the bot details */}
            <div id="movie-details">
            <p style={{fontWeight:'lighter',textAlign:'center' ,fontSize:'13px'}}> {movie.subTitle}</p>
            {/* <img src='/images/watchlist-icon.svg' height="50px" width="50px" onClick={()=>AddtoWatchlist(movie)} alt='add'/> */}
          </div>

          </div> 



          ))  
          }

    </div>
    <Outlet/>
  </div>
    </div>


  )
}

export default Display
