import React ,{useState} from 'react'
import {NavLink} from 'react-router-dom'

function NavBar({movies,setMovies}) {

  // Search
const [searchItem, setSearchItem] = useState(" ");

//  Create a search function 
 function searchAnything(event){

    const matchItems = movies.filter((dt=>{
        return ` ${dt.title}`
        .toLowerCase()
        .includes(event.target.value.toLowerCase())
    }))
    setMovies(matchItems);

    }

  return (
    <div className='nav'>
     <nav>



    <img src='https://img.icons8.com/color/48/null/imdb.png' alt='imdb logo' className='logo'/>
    <NavLink to="/"/>
    <NavLink to="home">
      <img src='https://img.icons8.com/material-outlined/24/228BE6/home--v2.png' className='icon-logo'/>HOME</NavLink> 
    <div id="search-bar">
          <NavLink>
                <input 
                    type='text'
                    placeholder = "Search" 
                    onChange={searchAnything} />
                    </NavLink>

       </div>
       <NavLink to="watch">
        <img src='https://img.icons8.com/ios/50/000000/add--v1.pnghttps://img.icons8.com/material-outlined/24/228BE6/add.png' className='icon-logo'/>WATCHLIST</NavLink>

       <NavLink className="login-btn">
        <button className='login-btn1'>LOGIN</button>
       </NavLink>

    </nav>
    </div>
  )
}

export default NavBar
