import React,{useState,useEffect} from 'react'
import { useParams,useNavigate} from "react-router-dom";

function SingleMovie({AddtoWatchlist}) {
    const [singlemovie,setSingleMovie] =useState(" ");

    const nav = useNavigate()
    const {id} = useParams()
    useEffect(()=>{
      fetch(`http://localhost:3000/movies/${id}`)
      .then(res=>res.json())
      .then(singlemovie=>{
        setSingleMovie(singlemovie)     
      })
    }, [])
    const goBack = () => {
      nav(-1);}

  return (
    <div>
        {
          <div key={singlemovie.id}>
             <div>
            <button onClick={goBack}>Go back</button>
            <img src={singlemovie.backgroundImg} className='img-fluid ' alt='image loading...' />
            <h5 className='centered-title'> {singlemovie.title}</h5>
            {/* style the bot details */}
            <div id="movie-details">
            <p cla>{singlemovie.description}</p>
            <img src='/images/watchlist-icon.svg' height="50px" width="50px" onClick={() => AddtoWatchlist(singlemovie.id)}/>
          </div>

          </div> 
          </div> 
          }
   </div>
  )
}

export default SingleMovie;
