import React from 'react'


function WatchList({watchlist,setWatchlist,removefromWatchlist,handleDelete}) {
  return (
    <div className='watchlist'>
    <div className=' container my-5'> 
     {/* render the bots in cards */}
    <div className=" row ">
        {
          watchlist.map((movie)=>(

            <div key={movie.id} className="col-4 mb-3  mr-3 px-3">
            <div className='border bg-light overflow-hidden rounded'> 
              <img src={movie.cardImg} className='img-fluid ' alt='loading...'/>
              <h5 style={{textAlign:'center',fontWeight:'lighter'}}>{movie.title}</h5>
              {/* style the bot details */}
              <div id="movie-details">
              <p style={{fontWeight:'lighter' ,fontSize:'13px'}}> {movie.subTitle}</p>
            <img src="https://img.icons8.com/ios/50/null/minus.png" onClick={()=>removefromWatchlist(movie)} className='remove-icon'/>
             <img src="https://img.icons8.com/color/48/null/delete-forever.png" onClick={()=>handleDelete(movie.id)} className='delete-icon'/>
          </div>
          </div> 
          </div>

          ))  
          }
    </div>

  </div>
  </div>

  )
}

export default WatchList
