import React, { useContext } from 'react'
import { MovieContext } from '../context/MovieContext';
import { AuthContext } from '../context/AuthContext';


function Homepage() {
const {movies,Addtowatchlist,deleteMovie} = useContext(MovieContext)
const {currentuser} = useContext(AuthContext)

const groupedProducts = {};
  // eslint-disable-next-line array-callback-return
  movies && movies.map((movie) => {
    if (groupedProducts[movie.type]) {
      groupedProducts[movie.type].push(movie);
    } else {
      groupedProducts[movie.type] = [movie];
    }
  });
  return (

    <div className='container mt-5 mb-5 bg-yellow-300'>
    {Object.keys(groupedProducts).map((type) => (
      <div key={type} className='category-container'>
        <h3 className='category-title py-3' style={{ color: '#00a082ff', fontFamily: 'cursive' }}>{type}</h3>
        <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4'>
          {groupedProducts[type].map((movie) => (
            <div key={movie.id} className='bg-white rounded-md shadow-md p-4'>
              <div className='text-center'>
                <img src={movie.cardImg} className='h-48 mx-auto' alt='loading...' />
              </div>
              <h5 className='text-center font-light'>{movie.title.toUpperCase()}</h5>
              <p className='text-center text-sm font-light'>{movie.subTitle}</p>
              <div className='flex flex-col items-start'>
                <button className='btn btn-outline-success btn-sm mb-2 mx-auto' onClick={() => Addtowatchlist(movie)}>Add to checkout</button>
              </div>
              {currentuser && currentuser.is_admin &&
                <button className='btn btn-outline-danger btn-sm mb-2 mx-auto' onClick={() => { deleteMovie(movie.id) }}>Delete</button>
              }
            </div>
          ))}
        </div>
      </div>
    ))}
  </div>


  )
}

export default Homepage
