import React from 'react'


function Display({movies,setMovies,AddtoWatchlist}) {
  return (
    <div>
         <div className=' container my-5'>
     {/* render the bots in cards */}
    <div className=" row ">
        {
          movies.map((movie)=>( 
          <div key={movie.id} className="col-3 mb-4  mr-3 px-3">
          <div className='border bg-light overflow-hidden'  >
             <img src={movie.poster} className='img-fluid ' alt='image loading...' onClick={()=> AddtoWatchlist(movie)}/>
            <h5 style={{textAlign:'center',fontWeight:'bolder'}}>{movie.title}</h5>
            {/* style the bot details */}
            <hr />
            <div id="bot-details">
            <p> {movie.genres}</p>
            <p>{movie.rating}</p>
            <p>{movie.language}</p>
          </div>
          </div> 
          </div>
          ))  
          }
    </div>

  </div>
    </div>
  )
}

export default Display
