import React,{useState,useEffect} from 'react'
import Display from './Display'
import WatchList from './WatchList'
import {Routes,Route} from 'react-router-dom'
import SingleMovie from './SingleMovie'
import Landing from './Landing'
import NavBar from './NavBar'
import AddMovie from './AddMovie'


function Renderitems() {
const [movies, setMovies] =useState([])
const [watchlist,setWatchlist]=useState([])

const AddtoWatchlist = (movie) => {
    const newwatchList = watchlist.filter((b) => b.id !== movie.id);
    setWatchlist([...newwatchList, movie]);
  };

  const removefromWatchlist = (movie) => {
    const newwatchList = watchlist.filter((b) => b.id !== movie.id);
    setWatchlist(newwatchList);
  };

useEffect(()=>{
    fetch("https://movies-csku.onrender.com/movies")
    .then(res => res.json())
    .then(data=>{
        setMovies(data)
    })

},[])

function handleDelete(id){
  fetch(`https://movies-csku.onrender.com/movies/${id}`,{
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
    }, 
  })
  setMovies(movies.filter((movie) => movie.id !== id));
  setWatchlist(watchlist.filter((movie) => movie.id !== id))
}
  return (
    <div>
      <NavBar movies={movies} setMovies={setMovies}/>
     <Routes>
    <Route exact path='/' element={<Landing />}/>

    <Route path="watch" element={<WatchList watchlist={watchlist} setWatchlist={setWatchlist} removefromWatchlist={removefromWatchlist} handleDelete={handleDelete}/> }/>
   <Route path='addmovie' element={<AddMovie />}/>
    <Route path="home" element={<Display movies={movies} setMovies={setMovies} AddtoWatchlist={AddtoWatchlist}/>}>

     <Route path="movie/:id" element={< SingleMovie AddtoWatchlist={AddtoWatchlist} />} />
   </Route>
    </Routes>

    </div>
  )
}

export default Renderitems
